import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Rocket, Globe, Lock, Monitor, Wrench, Sparkles, Settings, AlertCircle, CheckCircle } from 'lucide-react';
import { twMerge } from 'tailwind-merge';
import './App.css';

// ─── Aura Core: Unified state – single const array for all active deployments (edit names here) ───
const ACTIVE_DEPLOYMENTS = [
  { id: '1', url: 'www.horizonaid.tech', progress: 72, color: 'green', status: 'ok' },
  { id: '2', url: 'akiraspacepro.io', progress: 45, color: 'orange', status: 'Error' },
  { id: '3', url: 'bloometapro.com', progress: 88, color: 'blue', status: 'ok' },
];

// JSON blueprint for the renderer (JSON-to-UI brain)
const siteBlueprint = [
  { type: 'Hero', id: 'hero', props: { placeholder: 'What would you like to build?' } },
  { type: 'DeploymentCard', id: 'deployments', props: {} },
  { type: 'InteractiveDock', id: 'dock', props: {} },
];

const DOCK_ITEMS = [
  { id: 'deploy', label: 'Deploy', Icon: Rocket, color: '#22c55e' },
  { id: 'domains', label: 'Domains', Icon: Globe, color: '#3b82f6' },
  { id: 'ssl', label: 'SSL', Icon: Lock, color: '#eab308' },
  { id: 'monitor', label: 'Monitor', Icon: Monitor, color: '#a855f7' },
  { id: 'fix', label: 'Fix', Icon: Wrench, color: '#f97316' },
  { id: 'automate', label: 'Automate', Icon: Sparkles, color: '#ec4899' },
  { id: 'settings', label: 'Settings', Icon: Settings, color: '#94a3b8' },
];

function ProgressBar({ progress, color }) {
  const fillClass =
    color === 'green' ? 'progress-fill-green' : color === 'orange' ? 'progress-fill-orange' : 'progress-fill-blue';
  return (
    <div className="progress-track">
      <div className={twMerge('progress-fill', fillClass)} style={{ width: `${Math.min(100, Math.max(0, progress))}%` }} />
    </div>
  );
}

// ─── Block components (used by renderPage) ───
function HeroBlock({ props: blockProps = {}, context }) {
  const placeholder = blockProps.placeholder || 'What would you like to build?';
  return (
    <div className="w-full max-w-2xl mb-10">
      <div className="relative flex items-center rounded-full bg-white/5 border border-white/10 focus-within:border-white/20 focus-within:ring-2 focus-within:ring-white/10 transition-all duration-200">
        <span className="pl-5 flex-shrink-0 text-white/60">
          <Rocket className="w-5 h-5" />
        </span>
        <input
          type="text"
          placeholder={placeholder}
          className="w-full py-4 pl-3 pr-6 bg-transparent text-white placeholder-white/40 outline-none text-base"
          aria-label="Search or command"
        />
      </div>
    </div>
  );
}

function DeploymentCardBlock({ props: blockProps, context }) {
  const {
    frontCardId,
    setFrontCardId,
    deployments = [],
    openFullScreen,
    onFixDeployment,
    healingId,
  } = context || {};
  const selectedDeployment = deployments.find((d) => d.id === frontCardId);

  return (
    <div className="w-full flex flex-col items-center gap-6">
      {selectedDeployment && openFullScreen && (
        <div className="flex items-center gap-3">
          <button
            type="button"
            onClick={() => openFullScreen(selectedDeployment.id)}
            className="px-4 py-2 rounded-xl bg-white/10 border border-white/20 text-white/90 text-sm font-medium hover:bg-white/15 transition-colors"
          >
            Full Screen
          </button>
          <span className="text-white/50 text-sm">Selected: {selectedDeployment.url}</span>
        </div>
      )}
      <div className="w-full flex flex-wrap justify-center gap-6">
        {deployments.map((d) => {
          const isError = d.status === 'Error';
          const isHealing = healingId === d.id;
          return (
            <motion.div
              key={d.id}
              layout
              className={twMerge(
                'deployment-card deployment-card-glass relative w-full sm:w-[280px] rounded-2xl p-6 text-left cursor-pointer',
                frontCardId === d.id && 'pop-front',
                isHealing && 'healing-pulse'
              )}
              onClick={() => {
                setFrontCardId && setFrontCardId(frontCardId === d.id ? null : d.id);
                openFullScreen && openFullScreen(d.id);
              }}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              transition={{ type: 'spring', stiffness: 400, damping: 25 }}
            >
              <div className="flex items-center justify-between gap-2 mb-2">
                <span className="text-white/90 font-medium text-lg truncate" title={d.url}>
                  {d.url}
                </span>
                {isError && (
                  <span className="flex-shrink-0 text-red-400" title="Error">
                    <AlertCircle className="w-5 h-5" />
                  </span>
                )}
                {d.status === 'ok' && (
                  <span className="flex-shrink-0 text-emerald-400" title="OK">
                    <CheckCircle className="w-5 h-5" />
                  </span>
                )}
              </div>
              <ProgressBar progress={d.progress} color={d.color} />
              <div className="mt-2 text-white/40 text-sm">{d.progress}% complete</div>
              {/* Self-Heal: glowing Fix button when status is Error */}
              {isError && (
                <motion.button
                  type="button"
                  initial={{ opacity: 0, y: 8 }}
                  animate={{ opacity: 1, y: 0 }}
                  className={twMerge(
                    'fix-btn-glow mt-4 w-full py-2.5 rounded-xl font-medium text-sm flex items-center justify-center gap-2',
                    'bg-orange-500/20 border border-orange-400/40 text-orange-300 hover:bg-orange-500/30 transition-colors'
                  )}
                  onClick={(e) => {
                    e.stopPropagation();
                    onFixDeployment && onFixDeployment(d.id);
                  }}
                  disabled={!!healingId}
                >
                  <Wrench className="w-4 h-4" />
                  Fix
                </motion.button>
              )}
            </motion.div>
          );
        })}
      </div>
    </div>
  );
}

function InteractiveDockBlock({ props: blockProps, context }) {
  const { activeView, setActiveView, dockItems = DOCK_ITEMS } = context || {};
  return (
    <nav className="fixed bottom-6 left-1/2 -translate-x-1/2 z-20" aria-label="Main navigation">
      <div className="dock flex items-center gap-1 px-4 py-3 rounded-2xl border border-white/10">
        {dockItems.map((item) => (
          <button
            key={item.id}
            type="button"
            onClick={() => setActiveView && setActiveView(item.id)}
            className={twMerge(
              'p-3 rounded-xl transition-all duration-200 hover:bg-white/10',
              activeView === item.id && 'bg-white/15'
            )}
            style={{
              color: item.color,
              opacity: activeView === item.id ? 1 : 0.5,
            }}
            title={item.label}
            aria-label={item.label}
            aria-pressed={activeView === item.id}
          >
            <item.Icon className="w-6 h-6" />
          </button>
        ))}
      </div>
    </nav>
  );
}

const COMPONENT_MAP = {
  Hero: HeroBlock,
  DeploymentCard: DeploymentCardBlock,
  InteractiveDock: InteractiveDockBlock,
};

// ─── Aura Core: renderPage – JSON-to-UI renderer (the brain) ───
const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: { staggerChildren: 0.12, delayChildren: 0.06 },
  },
};

const itemVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: {
    opacity: 1,
    y: 0,
    transition: { duration: 0.4, ease: [0.25, 0.46, 0.45, 0.94] },
  },
};

function renderPage(blueprint, context) {
  return (
    <motion.div
      className="aura-renderer flex flex-col items-center pt-12 pb-28 px-4 w-full"
      variants={containerVariants}
      initial="hidden"
      animate="visible"
    >
      {blueprint.map((block, index) => {
        const Component = COMPONENT_MAP[block.type];
        if (!Component) return null;
        return (
          <motion.div
            key={block.id || index}
            variants={itemVariants}
            className="w-full max-w-4xl flex flex-col items-center"
          >
            <Component props={block.props || {}} context={context} />
          </motion.div>
        );
      })}
    </motion.div>
  );
}

// ─── Full-screen setup view (DNS & SSL) with Framer Motion zoom ───
function FullScreenSetup({ deployment, onClose, closing }) {
  return (
    <motion.div
      className="full-screen-overlay"
      initial={{ opacity: 0 }}
      animate={{ opacity: closing ? 0 : 1 }}
      exit={{ opacity: 0 }}
      transition={{ duration: 0.25 }}
    >
      <motion.div
        className="full-screen-card max-w-lg"
        onClick={(e) => e.stopPropagation()}
        initial={{ opacity: 0, scale: 0.9 }}
        animate={{ opacity: 1, scale: closing ? 0.95 : 1 }}
        exit={{ opacity: 0, scale: 0.95 }}
        transition={{ type: 'spring', damping: 25, stiffness: 300 }}
      >
        <div className="flex justify-between items-start mb-4">
          <h3 className="text-xl font-semibold text-white/95 truncate pr-4">{deployment?.url}</h3>
          <button
            type="button"
            onClick={onClose}
            className="flex-shrink-0 p-2 rounded-lg text-white/70 hover:text-white hover:bg-white/10 transition-colors"
            aria-label="Close"
          >
            <span className="text-lg font-bold">×</span>
          </button>
        </div>
        <ProgressBar progress={deployment?.progress ?? 0} color={deployment?.color ?? 'green'} />
        <p className="mt-2 mb-4 text-white/50 text-sm">{deployment?.progress ?? 0}% complete</p>

        <div className="full-screen-section">
          <div className="full-screen-section-title">DNS</div>
          <div className="full-screen-section-content">
            A record: <code className="text-emerald-400">{deployment?.url ?? ''}</code> → Production IP
          </div>
        </div>
        <div className="full-screen-section">
          <div className="full-screen-section-title">SSL</div>
          <div className="full-screen-section-content">
            Certificate: Let&apos;s Encrypt • Auto-renewal enabled
          </div>
        </div>
      </motion.div>
      <button
        type="button"
        className="absolute inset-0 -z-10"
        onClick={onClose}
        aria-label="Close overlay"
      />
    </motion.div>
  );
}

function App() {
  const [showSourceCode, setShowSourceCode] = useState(false);
  const [activeView, setActiveView] = useState('deploy');
  const [frontCardId, setFrontCardId] = useState(null);
  const [fullScreenId, setFullScreenId] = useState(null);
  const [closingOverlay, setClosingOverlay] = useState(false);
  const [healingId, setHealingId] = useState(null);
  const [deployments, setDeployments] = useState(() => [...ACTIVE_DEPLOYMENTS]);

  const fullScreenDeployment = fullScreenId ? deployments.find((d) => d.id === fullScreenId) : null;

  const openFullScreen = (id) => setFullScreenId(id);

  const closeFullScreen = () => {
    setClosingOverlay(true);
    setTimeout(() => {
      setFullScreenId(null);
      setClosingOverlay(false);
    }, 250);
  };

  const onFixDeployment = (id) => {
    setHealingId(id);
    setTimeout(() => {
      setDeployments((prev) =>
        prev.map((d) => (d.id === id ? { ...d, status: 'ok' } : d))
      );
      setHealingId(null);
    }, 600);
  };

  useEffect(() => {
    if (!fullScreenId) return;
    const onKey = (e) => {
      if (e.key === 'Escape') closeFullScreen();
    };
    window.addEventListener('keydown', onKey);
    return () => window.removeEventListener('keydown', onKey);
  }, [fullScreenId]);

  const auraContext = {
    activeView,
    setActiveView,
    frontCardId,
    setFrontCardId,
    deployments,
    dockItems: DOCK_ITEMS,
    openFullScreen,
    onFixDeployment,
    healingId,
  };

  const jsonString = JSON.stringify(siteBlueprint, null, 2);

  return (
    <div className="app-bg text-white flex flex-col">
      <div className="glow-orb glow-orb-tl" aria-hidden />
      <div className="glow-orb glow-orb-tr" aria-hidden />
      <div className="glow-orb glow-orb-bl" aria-hidden />
      <div className="glow-orb glow-orb-br" aria-hidden />

      <div className="builder-toolbar">
        <button
          type="button"
          onClick={() => setShowSourceCode(!showSourceCode)}
          className={twMerge('builder-toggle', showSourceCode && 'active')}
        >
          Source Code
        </button>
      </div>

      {showSourceCode ? (
        <div className="builder-split">
          <div className="builder-panel builder-panel-json">
            <div className="builder-panel-header">Blueprint JSON</div>
            <pre className="builder-json">{jsonString}</pre>
          </div>
          <div className="builder-panel builder-panel-live">
            <div className="builder-panel-header">Live Preview</div>
            <div className="builder-live-inner">
              {renderPage(siteBlueprint, auraContext)}
            </div>
          </div>
        </div>
      ) : (
        <main className="main-screen">
          {renderPage(siteBlueprint, auraContext)}
        </main>
      )}

      <AnimatePresence mode="wait">
        {fullScreenDeployment && (
          <FullScreenSetup
            key={fullScreenId}
            deployment={fullScreenDeployment}
            onClose={closeFullScreen}
            closing={closingOverlay}
          />
        )}
      </AnimatePresence>
    </div>
  );
}

export default App;
