function getDominantColorsFromImage(file, callback) {
  const img = new Image();
  const url = URL.createObjectURL(file);
  img.onload = () => {
    try {
      const canvas = document.createElement('canvas');
      const scale = 0.15;
      canvas.width = Math.max(1, Math.floor(img.width * scale));
